package blokplugins.kitroom.database;

import org.bukkit.entity.Player;
import org.bukkit.plugin.java.JavaPlugin;

import java.sql.*;

public class MySQL implements DatabaseManager {
    private final JavaPlugin plugin;
    private Connection connection;

    // Add MySQL configuration fields
    private final String host;
    private final int port;
    private final String database;
    private final String username;
    private final String password;

    public MySQL(JavaPlugin plugin) {
        this.plugin = plugin;
        // Load MySQL configuration from plugin's config.yml
        this.host = plugin.getConfig().getString("mysql.host");
        this.port = plugin.getConfig().getInt("mysql.port");
        this.database = plugin.getConfig().getString("mysql.database");
        this.username = plugin.getConfig().getString("mysql.username");
        this.password = plugin.getConfig().getString("mysql.password");
    }

    @Override
    public void connect() {
        try {
            // Load MySQL JDBC driver
            Class.forName("com.mysql.cj.jdbc.Driver");

            String url = String.format(
                    "jdbc:mysql://%s:%d/%s?useSSL=false&autoReconnect=true",
                    host, port, database
            );

            connection = DriverManager.getConnection(url, username, password);

            // Modified table creation queries for MySQL
            String createTableSQLplayer = "CREATE TABLE IF NOT EXISTS players (" +
                    "uuid VARCHAR(36) PRIMARY KEY, " +
                    "k1 TEXT DEFAULT NULL, k2 TEXT DEFAULT NULL, k3 TEXT DEFAULT NULL, " +
                    "k4 TEXT DEFAULT NULL, k5 TEXT DEFAULT NULL, k6 TEXT DEFAULT NULL, " +
                    "k7 TEXT DEFAULT NULL, k8 TEXT DEFAULT NULL, k9 TEXT DEFAULT NULL, " +
                    "ec1 TEXT DEFAULT NULL, ec2 TEXT DEFAULT NULL, ec3 TEXT DEFAULT NULL, " +
                    "ec4 TEXT DEFAULT NULL, ec5 TEXT DEFAULT NULL, ec6 TEXT DEFAULT NULL, " +
                    "ec7 TEXT DEFAULT NULL, ec8 TEXT DEFAULT NULL, ec9 TEXT DEFAULT NULL" +
                    ")";

            String createTableSQLKitroom = "CREATE TABLE IF NOT EXISTS kitroom (" +
                    "name VARCHAR(255) PRIMARY KEY UNIQUE," +
                    "kit TEXT" +
                    ")";

            try (Statement query = connection.createStatement()) {
                query.execute(createTableSQLplayer);
                query.execute(createTableSQLKitroom);
            }

            // Insert default records using MySQL syntax
            String insertDefaultKits = "INSERT IGNORE INTO kitroom (name, kit) VALUES (?, ?)";
            try (PreparedStatement statement = connection.prepareStatement(insertDefaultKits)) {
                String[] defaultKits = {"equipment", "pvp", "potions"};
                for (String kit : defaultKits) {
                    statement.setString(1, kit);
                    if(kit.equals("potions")) {
                        statement.setString(2, "");
                    } else if(kit.equals("pvp")) {
                        statement.setString(2, "");
                    } else if(kit.equals("equipment")) {
                        statement.setString(2, "");
                    }
                    statement.executeUpdate();
                }
            }

            plugin.getLogger().info("[MySQL] Connected to MySQL database");
        } catch (ClassNotFoundException e) {
            plugin.getLogger().severe("[MySQL] MySQL JDBC driver not found!");
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Connection failed: " + e.getMessage());
        }
    }

    @Override
    public void disconnect() {
        try {
            if (connection != null && !connection.isClosed()) {
                connection.close();
                plugin.getLogger().info("[MySQL] Disconnected from MySQL database");
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Disconnection failed: " + e.getMessage());
        }
    }

    @Override
    public void playerJoin(Player p) {
        String insertPlayerSQL = "INSERT IGNORE INTO players (uuid) VALUES (?)";
        try (PreparedStatement statement = connection.prepareStatement(insertPlayerSQL)) {
            statement.setString(1, p.getUniqueId().toString());
            statement.executeUpdate();
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Failed to insert player: " + e.getMessage());
        }
    }

    @Override
    public String getKit(Player p, String kitType) {
        String kit = null;
        String querySQL = "SELECT " + kitType + " FROM players WHERE uuid = ?";
        try (PreparedStatement statement = connection.prepareStatement(querySQL)) {
            statement.setString(1, p.getUniqueId().toString());
            ResultSet resultSet = statement.executeQuery();
            if (resultSet.next()) {
                kit = resultSet.getString(kitType);
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Failed to fetch kit: " + e.getMessage());
        }
        return kit;
    }

    @Override
    public void saveKit(Player p, String kitType, String kitData) {
        String updateSQL = "UPDATE players SET " + kitType + " = ? WHERE uuid = ?";
        try (PreparedStatement statement = connection.prepareStatement(updateSQL)) {
            statement.setString(1, kitData);
            statement.setString(2, p.getUniqueId().toString());
            statement.executeUpdate();
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Failed to save kit: " + e.getMessage());
        }
    }

    @Override
    public void saveKitRoom(String name, String kit) {
        // MySQL UPSERT syntax
        String updateSQL = "INSERT INTO kitroom (name, kit) VALUES (?, ?) " +
                "ON DUPLICATE KEY UPDATE kit = VALUES(kit)";
        try (PreparedStatement statement = connection.prepareStatement(updateSQL)) {
            statement.setString(1, name);
            statement.setString(2, kit);
            statement.executeUpdate();
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Failed to save kitroom: " + e.getMessage());
        }
    }

    @Override
    public String getKitRoom(String name) {
        String kit = null;
        String querySQL = "SELECT kit FROM kitroom WHERE name = ?";
        try (PreparedStatement statement = connection.prepareStatement(querySQL)) {
            statement.setString(1, name);
            ResultSet resultSet = statement.executeQuery();
            if (resultSet.next()) {
                kit = resultSet.getString("kit");
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("[MySQL] Failed to get kitroom: " + e.getMessage());
        }
        return kit;
    }
}